import { getHits } from "../../actions/getHits";

import Thumbnail from "./Thumbnail";

const HitsComponent = () => {
  return (
    <div className="hits-container">
      <Configure
        hitsPerPage={21}
        // userToken={persona}
        // filters={shop ? `shop_availability:'${shop}'` : ''}
      />

      {/* Product thumbnail */}
      <Thumbnail />

      <CustomHits />
      <Pagination />
    </div>
  );
};

export default HitsComponent;

